/*global GmCXt,mg$*/

let modal = {};

modal.addPublishActions = function(onOk) {

    let self = modal;
    let envs = rootScope.appEnvs;

    function updateMessage() {
        let msg = rootScope.labels.guideWillPubIn;
        let showMsg = false;
        let envStr = "";

        mg$('.mgPlayerJSProd_active-progress').each(function(index, element) {
            showMsg = true;
            let en = mg$(element).text();
            if (!envStr) {
                envStr = en;
            } else {
                envStr = envStr + ", " + en;
            }
        });

        if(isPublishedOnTopEnv()){
            mg$("#mgPlayerJSProd_publishMsg").text(rootScope.labels.guideIsPubIn + envStr);
        }
        else if (showMsg) {
            mg$('#mgPlayerJSProd_publishMsg').text(msg + envStr);
        } else {
            mg$('#mgPlayerJSProd_publishMsg').text('');
        }


    }

    function toggleActive(e) {
        let k = envs.indexOf(e.currentTarget.id);
        mg$("#mgPlayerJSProd_checkbox-tour-schedule-pub").prop('checked', false);
        mg$(".mgPlayerJSProd_pub-date-container").hide();

        if (mg$(e.currentTarget).hasClass('mgPlayerJSProd_active-progress')) {
            mg$(e.currentTarget).removeClass('mgPlayerJSProd_active-progress');
            mg$(".mgPlayerJSProd_pub-schedule-checkbox-container").hide();
            if (k === 0) {
                mg$('.mgPlayerJSProd_ege-publish').addClass("mgPlayerJSProd_disabled");
            }else {
                mg$('.mgPlayerJSProd_ege-publish').removeClass("mgPlayerJSProd_disabled");
            }
            var j = k + 1;
            while (j < envs.length) {
                mg$('#' + envs[j]).removeClass('mgPlayerJSProd_active-progress');
                j++;
            }

        } else {
            mg$(e.currentTarget).addClass('mgPlayerJSProd_active-progress');
            mg$('.mgPlayerJSProd_ege-publish').removeClass("mgPlayerJSProd_disabled");
            if (k > 0) {
                var j = 0;
                while (j < k) {
                    mg$('#' + envs[j]).addClass('mgPlayerJSProd_active-progress');
                    j++;
                }
            }
            if (envs[k] === envs[envs.length - 1]) {
                mg$(".mgPlayerJSProd_pub-schedule-checkbox-container").show();
            }
        }

        if(isPublishedOnTopEnv()){
            mg$('.mgPlayerJSProd_ege-publish').addClass("mgPlayerJSProd_disabled");
        }
        updateMessage();
    }

    for (let i = 0; i < envs.length; i++) {
        mg$('#' + envs[i]).on('click', toggleActive);
    }

    mg$('.mgPlayerJSProd_ege-publish').off('click').on('click', function() {

        let env = [];
        mg$('.mgPlayerJSProd_active-progress').each(function(index, element) {
            env.push(mg$(element).attr('id'));
        });
        let guideActiveEnv = modal.getGuideActiveEnvs();

        if (!rootScope.guideEnvs || env.length > guideActiveEnv.length) {

            if (!env.length && rootScope.publishStatus === "publish") {
                return;
            }
            onOk(env, "publish");

        } else {
            let lastEnv = env[env.length - 1];
            let lastEnvIdx = rootScope.appEnvs.indexOf(lastEnv);

            if (lastEnvIdx >= 0) {

                let unpubList = rootScope.appEnvs.slice(lastEnvIdx + 1);
                if (unpubList.length) {
                    onOk(unpubList, 'unpublish');
                }
            }
        }

        self.close();
        localStorage.setItem(GmCXt.prevEnv, env);

    });

    mg$('.mgPlayerJSProd_ege-cancel-btn').off('click').on('click', function() {
        self.close();
    });

    mg$('.mgPlayerJSProd_popup-close').off('click').on('click', function() {
        self.close();
    });
};

modal.attachPopupActions = function(onOk, onCancel, onPreview, tour) {
    let self = modal;

    mg$('.mgPlayerJSProd_overlay-container').off('click').on('click', function(e) {
        self.close();
        if (onCancel) {
            onCancel();
        }
    });

    mg$('#mgPlayerJSProd_input-csv').on('change', function() {
        let fileName = '';
        if (this.files && this.files.length > 0) {
            fileName = this.files[0].name;
            mg$('#mgPlayerJSProd_selected-filename').text(fileName).removeClass('mgPlayerJSProd_display-none');
        } else {
            mg$('#mgPlayerJSProd_selected-filename').text('').addClass('mgPlayerJSProd_display-none');
        }
    });

    mg$('.mgPlayerJSProd_csv-download-wrapper').off('click').on('click', function(e) {
        rootScope.exportCsv(tour);
    });

    mg$('.mgPlayerJSProd_ege-ok-btn').off('click').on('click', function() {
        let el = document.getElementById("mgPlayerJSProd_donot-show-cb");
        if (el) {
            GmCXt.saveToStorage('confirmLinkGuidePub', el.checked);
        }
        self.close();
        if (onOk) {
            onOk();
        }
    });

    mg$('.mgPlayerJSProd_ege-ok-json-update').off('click').on('click', function() {
        onOk();
    });

    mg$('.mgPlayerJSProd_ege-ok-btn-radio').off('click').on('click', function() {

        let envs = mg$('.mgPlayerJSProd_env');

        let envVal = '';

        if (envs) {
            envVal = mg$('.mgPlayerJSProd_env:checked').val();
        }

        if (envVal) {
            onOk(envVal);
            self.close();
        }
    });

    mg$('.mgPlayerJSProd_ege-option-btn').off('click').on('click', function() {
        if (onPreview)
            onPreview();
        self.close();
    });

    mg$('.mgPlayerJSProd_ege-cancel-btn').off('click').on('click', function() {
        if (onCancel)
            onCancel();
        self.close();
    });

    mg$('.mgPlayerJSProd_ege-popup-close').off('click').on('click', function() {
        if (onCancel)
            onCancel();
        self.close();
    });

    mg$('#mgPlayerJSProd_resusme-guide-close-button').off('click').on('click', function() {
        self.close();
    });

    mg$('.mgPlayerJSProd_ege-update-btn').off('click').on('click', function() {
        let audioText = mg$('#audio-text-area').val();
        audioText = audioText.trim();

        if (!audioText.length) {
            mg$('#audio-text-area').addClass('empty-textarea');
            return true;
        }

        if (onOk)
            onOk(audioText);
        self.close();
    });

    mg$('.mgPlayerJSProd_ege-email-submit-btn').off('click').on('click', function() {
        let email_id = mg$('.mgPlayerJSProd_ege-popup-email-input').val().trim();

        if (!email_id.length) {
            mg$('.mgPlayerJSProd_ege-popup-error-msg').show();
            return true;
        }

        if (onOk)
            onOk(email_id);
        self.close();
    });

    mg$('.mgPlayerJSProd_ege-otp-submit-btn').off('click').on('click', function() {
        let email_id = mg$('.mgPlayerJSProd_ege-popup-email-input-confirm').val().trim();
        let otp = mg$('.mgPlayerJSProd_ege-popup-confirm-otp').val().trim();

        if (!otp.length) {
            mg$('.mgPlayerJSProd_ege-popup-error-msg').show();
            return true;
        }

        if (onOk)
            onOk(email_id, otp);
        self.close();
    });

    if (mg$('#audio-text-area').length) {
        mg$('#audio-text-area').on('focus', function(e) {
            mg$(e.target).removeClass('empty-textarea');
        });
    }

    if (mg$('#preview-link').length) {
        mg$('#preview-link').off('click').on('click', function() {
            let audioText = mg$('#audio-text-area').val();
            audioText = audioText.trim();
            if (onPreview && audioText.length)
                onPreview(audioText);
        });
    }

    mg$('.mgPlayerJSProd_modal-view-more').off('click').on('click', function() {
        mg$(".mgPlayerJSProd_info-msg").show();
        mg$('.mgPlayerJSProd_modal-view-more').hide();
    });

    mg$('.mgPlayerJSProd_ege-csv-ok-btn').off('click').on('click', function() {

        let files = mg$('#mgPlayerJSProd_input-csv')[0].files;

        let parsedata = [];

        let getParseCsvData = function(data) {
            let newLinebrk = data.split("\n");

            for (let i = 0; i < newLinebrk.length; i++) {
                if (newLinebrk[i] !== '')
                    parsedata.push(newLinebrk[i].split(","));
            }
        };

        if (files && files[0]) {
            let myFile = files[0];
            let reader = new FileReader();

            reader.onload = function(e) {

                let csvData = e.target.result;
                getParseCsvData(csvData); // calling function for parse csv data

                onOk(parsedata);
            };

            reader.readAsBinaryString(myFile);
        } else {
            onOk();
        }

        self.close();

    });
};

