rootScope.selectLanguage = function(lang) {
    if (GmCXt.userPrefLang) {
        if (rootScope.selectedLanguage === lang) {
            return true;
        }
    } else if (!lang) {
        return true;
    }
};

function getAllLanguagesArr(langSett) {
    let langArr = [];
    langArr.push(GmCXt.defaultLang);

    if (langSett.translations) {
        for (let i = 0, j = langSett.translations.length; i < j; i++) {
            if (!GmCXt.isEmpty(langSett.translations[i].language)) {
                langArr.push(langSett.translations[i]);
            }
        }
    } else {
        for (let i = 0, j = langSett.length; i < j; i++) {
            if (!GmCXt.isEmpty(langSett[i].language)) {
                langArr.push(langSett[i]);
            }
        }
    }

    return GmCXt.removeDuplicates(langArr);
}

rootScope.getActiveOrgAppLangSetting = function(isDefault) {
    let languageSettings = {};

    //initial language setting
    let langSett = {
        default: {
            language:  GmCXt.model.langOption.language._default,
            name:  GmCXt.model.langOption.name._default,
            voice:  GmCXt.model.langOption.voice._default
        },
        translations: []
    };

    // for westpac
    if (GmCXt.conf.appConfig.customer === 'westpac' && isDefault) {
        return {
            language: 'en-AU',
            name: '',
            voice: 'Catherine',
        };
    } else {
        //collecting language setting from organization
        if (GmCXt.organization?.admin_settings?.language_settings && !GmCXt.isEmpty(GmCXt.organization.admin_settings.language_settings.default)) {
            languageSettings = {...GmCXt.organization.admin_settings.language_settings};
        } else {
            //setting default language setting if organization language setting is not availabel
            languageSettings = {...langSett};
        }

        const appSetting = GmCXt.getAppSetting();
        if (appSetting && appSetting?.narrator) {
            const appNarrator = appSetting.narrator;
            const appNarratorArray = appSetting.narrator.split("-");
            const appNarratorVoice = appNarratorArray[appNarratorArray.length-1];
            const appNarratorLang = appNarrator.replace("-"+appNarratorVoice,"");
            if (appNarratorLang === languageSettings.default.language) {
                const appLangDefault = {
                    language: appNarratorLang,
                    name: languageSettings?.default?.name || "",
                    voice: appNarratorVoice
                }; 
                languageSettings.default = {...appLangDefault};
            }

            if (!GmCXt.isEmpty(appSetting.app_languages) && appSetting?.enable_application_language_settings) {
                languageSettings.translations = [...appSetting.app_languages];
            }
        }
        if (isDefault) {
            return languageSettings.default;
        } else {
            return languageSettings;
        }

    }
};

rootScope.getAppOrgLanguage = function(l) {
    if (rootScope.enableTranslation && GmCXt.organization) {
        const langSett = {...GmCXt.organization.admin_settings.language_settings};

        const appDefaultSett = rootScope.getActiveOrgAppLangSetting(true);
        rootScope.defaultLang = appDefaultSett;
        GmCXt.defaultLang = appDefaultSett;


        let defLanguage = rootScope.language;
        if (!defLanguage) defLanguage = rootScope.defaultLang.language;

        if (!GmCXt.isEmpty(l)) {
            rootScope.languages = getAllLanguagesArr(l);
        } else {
            rootScope.languages = getAllLanguagesArr(langSett);
        }

        rootScope.languages.filter(function(item) {
            if (item.language === defLanguage) {
                rootScope.selectedLanguage = item.name;
            }
        });
    } else {
        rootScope.languages = [];
    }
};

