// Helper function for making requests through iframe to bypass CORS
function makeIframeRequest(url, options = {}) {
    return new Promise((resolve, reject) => {
        try {
            // Create a unique request ID
            const requestId = 'sso_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
            const requestType = options.requestType || 'fetchSsoId';

            let iframe;

            // Set up message listener for the response
            const messageHandler = function(event) {
                if (event.data && event.data.action === 'ssoResult' && event.data.requestId === requestId) {
                    // Clean up
                    window.removeEventListener('message', messageHandler);
                    document.body.removeChild(iframe);
                    if (event.data.success) {
                        resolve(event.data.data);
                    } else {
                        reject(new Error(event.data.error || 'Failed to fetch data'));
                    }
                } else if (event.data && event.data.action === 'iframeReady') {
                    // When iframe signals it's ready, send the request
                    iframe.contentWindow.postMessage({
                        action: requestType,
                        url: url,
                        headers: options.headers || {},
                        credentials: options.credentials || 'omit',
                        requestId: requestId
                    }, '*');
                }
            };

            if(!document.getElementById('mgPlayerJSProd_url-fetcher')) {
                // Create a hidden iframe
                iframe = document.createElement('iframe');
                iframe.src = GmCXt.getBasePath('common/url_fetcher/url.html');
                iframe.style.display = 'none';
                iframe.id = "mgPlayerJSProd_url-fetcher";
                document.body.appendChild(iframe);
                window.addEventListener('message', messageHandler);
            } else {
                iframe.contentWindow.postMessage({
                    action: requestType,
                    url: url,
                    headers: options.headers || {},
                    credentials: options.credentials || 'omit',
                    requestId: requestId
                }, '*');
            }

            // Set a timeout in case the iframe doesn't load or respond
            setTimeout(() => {
                window.removeEventListener('message', messageHandler);
                if (document.body.contains(iframe)) {
                    document.body.removeChild(iframe);
                }
                reject(new Error('Timeout while fetching data'));
            }, GmCXt.t_.sec15); // 15 second timeout

        } catch (error) {
            GmCXt.log(1, "Error in iframe request:", error);
            reject(error);
        }
    });
}
rootScope.removeCookieWithSSO = function() {
    return new Promise(async (resolve, reject) => {
        try {
            rootScope.ssoId = rootScope.ssoId || GmCXt.user?.ssoId;

            if (GmCXt.isEmpty(GmCXt.conf.ssoRemoveCookieUrl) || GmCXt.isEmpty(rootScope.ssoId)) {
                resolve();
            }

            async function triggerRemoveCookie() {
                const currHost = GmCXt.trimAndLowerCaseURL(GmCXt.urlParts.host);
                const url = getSsoIdUrl(GmCXt.conf.ssoRemoveCookieUrl);

                GmCXt.trackApiCalls();

                // Set up headers for the request
                const headers = {
                    'x-mg-host': currHost,
                    'x-mg-source': GmCXt.conf.appName,
                    'x-mg-orgId': '', // Empty org ID
                    'Content-Type': 'application/json',
                };

                // Make the request through the iframe
                const jsonData = await makeIframeRequest(url, {
                    requestType: 'removeCookieSSO',
                    headers: headers,
                    credentials: 'include'
                });

                const parsedData = GmCXt.parseJSON(jsonData);
                GmCXt.activeApiCallCount--;
                rootScope.hideLoading();
                resolve(parsedData);
            }

            triggerRemoveCookie();

        } catch (error) {
            console.error("Error remove cookie SSO info:", error);
            reject(error);
        }
    });
};

