rootScope.showBranchView = function(tour) {
    return GmCXt.containBranchStep(tour) && (!tour.isEditMode || (tour.isEditMode && tour.isBranchTour));
};

rootScope.handleStartPointFound = function(data) {
    rootScope.contTour.step = data.step;
    resumeGuide();
    const s = rootScope.contTour.step;
    GmCXt.log(33, "Start Point Found" + GmCXt.stepLog(s.step_id, s.tour_id));

    if (data.resumeGuide) {
        GmCXt.startPointStepPlayed = s?.step_id + '-' + s?.tour_id;
    }

    delete rootScope.startPoints;
    delete rootScope.searching_sp;
};

rootScope.handleNextStepFound = function() {
    rootScope.stepFound = true;

    const s = rootScope.contTour.step;
    GmCXt.log(33, "FOUND next step" + GmCXt.stepLog(s.step_id, s.tour_id));
    resumeGuide();

};

rootScope.handleStartPointNotFound = function(data) {

    let cacheKey = data?.step?.step_id + '-' + rootScope.contTour?.tour?.tour_id;

    if(data.resumeGuide && GmCXt.startPointStepPlayed && GmCXt.startPointStepPlayed === cacheKey) {
        return;
    }

    if (rootScope.searching_sp < rootScope.startPoints?.length - 1) {
        rootScope.searching_sp += 1;
        const data = {
            "tour_id": rootScope.contTour.tour_id,
            "startPoint": rootScope.startPoints[rootScope.searching_sp],
        };

        GmCXt.startPointStepPlayed = '';
        GmCXt.sendMessageToAllWindows('mgPlayerJSProd_action:started;task:search_start_point', data);

    } else if (rootScope.contTour.tour.steps[0].step_type !== "inline") {
        //For playing first step which is not inline in startpoint guides
        continuePlay(
            "",
            null,
            rootScope.contTour.tour,
            rootScope.contTour.source,
            rootScope.contTour.tour.category_title
        );
    }
};

rootScope.showCookiePopup = function() {
    if (GmCXt.user && !GmCXt.isEmpty(GmCXt.user) && !GmCXt.user?.settings?.edcastTermsAccepted && !GmCXt.isClientJs() &&
    rootScope.isUserLoggedIn() &&  !rootScope.isSuperAdmin() && !rootScope.isMgAdmin() &&
    !rootScope.isPartnerAdmin() && GmCXt.organization?.admin_settings?.display_accept_cookie_popup) {
        return true;
    } else {
        return false;
    }
};

rootScope.closeCookiePopup = function() {
    GmCXt.user.settings.edcastTermsAccepted = true;

    let user = {...GmCXt.user};
    user.settings = JSON.stringify(GmCXt.user.settings);

    function onSuccess(response) {
        mg$(".mgPlayerJSProd_cookie-policy-popup-container").remove();
        rootScope.saveUser(GmCXt.user);
    }

    if (GmCXt.isAnonymousUser()) {
        rootScope.displayAcceptCookiePopup = false;
    } else {
        if (GmCXt.user.signin_user_email) {
            let data = {
                email_id: GmCXt.user.signin_user_email,
                settings: user.settings,
                organization_id: GmCXt.user.organization_id
            };

            GmCXt.setAnonymousUserPrefrence(data).then(onSuccess);
        } else {
            let data = {
                settings : {
                    edcastTermsAccepted : true
                }
            };
            GmCXt.updateUserSettings(data, GmCXt.user, onSuccess);
        }
    }
};

rootScope.setContainerHeight = function(view) {
    rootScope.containerView = view;
    GmCXt.timeout(function() {
        let panelHeight = mg$('.mgPlayerJSProd_panel').innerHeight() ? mg$('.mgPlayerJSProd_panel').innerHeight() : 0;
        let footerHeight = mg$('#mgPlayerJSProd_footer-panel-container').innerHeight() || 0;
        let headerHeight = mg$('#mgPlayerJSProd_ege-header').innerHeight() || 0;
        let headerActionBoxHeight = mg$('.mgPlayerJSProd_guide-view-header-panel-container').innerHeight() || 0;
        let panelHeaderHeight = mg$('.mgPlayerJSProd_panel-header-wrapper').outerHeight(true) ? mg$('.mgPlayerJSProd_panel-header-wrapper').outerHeight(true) : 0;
        let bottomButtonWrapper = mg$('.mgPlayerJSProd_tour-btn-wrapper').innerHeight() || 0;
        let listPageHeaderHeight = mg$('.mgPlayerJSProd_list-page-header').innerHeight() ? mg$('.mgPlayerJSProd_list-page-header').innerHeight() : 0;
        let containerHeight = 0;
        let stepEditFooterHeight = mg$('.mgPlayerJSProd_step-edit-footer-wrapper').innerHeight() || 0;

        switch (view) {
        case 'signin':
            containerHeight = panelHeight - headerHeight - footerHeight + 8;
            mg$('.mgPlayerJSProd_signin-page-container').innerHeight(containerHeight);

            if (mg$('.mgPlayerJSProd_signin-page-container').innerHeight() < 60) {
                mg$('.mgPlayerJSProd_header-banner-wrapper').hide();
            } else {
                mg$('.mgPlayerJSProd_header-banner-wrapper').removeAttr('style');
            }
            break;

        case 'currentPage':
            let searchboxHeight = mg$('.mgPlayerJSProd_list-page-header').innerHeight() || 0;
            containerHeight = panelHeight - footerHeight - headerHeight - searchboxHeight - 5;
            mg$('.mgPlayerJSProd_category-list').innerHeight(containerHeight);

        case 'addCategory':
            containerHeight = panelHeight - headerHeight - mg$('.mgPlayerJSProd_btn-wrapper').innerHeight() - footerHeight -
            mg$('.mgPlayerJSProd_header-title').innerHeight();
            mg$('.mgPlayerJSProd_form-wrapper').innerHeight(containerHeight);
            mg$('.mgPlayerJSProd_btn-wrapper').css('bottom', footerHeight);
            break;

        case 'allguides':
        case 'stepContainer':
            let listPageBtnHeight = mg$('.mgPlayerJSProd_list-page-btn-wrapper').innerHeight() ?
                mg$('.mgPlayerJSProd_list-page-btn-wrapper').innerHeight() : 0;

            let breadCrumsHeight = mg$('#mgPlayerJSProd_breadcrums-main-container').innerHeight() ?
                mg$('#mgPlayerJSProd_breadcrums-main-container').innerHeight() : 0;

            containerHeight = panelHeight - (headerHeight + headerActionBoxHeight + stepEditFooterHeight +
            listPageBtnHeight + listPageHeaderHeight + breadCrumsHeight + footerHeight);
            mg$('.mgPlayerJSProd_category-list').innerHeight(containerHeight);
            break;

        case 'search':
            containerHeight = panelHeight - (headerActionBoxHeight + headerHeight +
                footerHeight + listPageHeaderHeight + 5);

            mg$('.mgPlayerJSProd_search-list').innerHeight(containerHeight);
            break;

        case 'chatbot':
            let chatHeaderHeight = mg$('.mgPlayerJSProd_chat-page-header').innerHeight() || 0;
            containerHeight = panelHeight - (panelHeaderHeight + chatHeaderHeight + footerHeight + 5);
            mg$('#mgPlayerJSProd_uilb-main-container').innerHeight(containerHeight);
            break;

        case 'imageStep':
        case 'messageStep':
        case 'inlineStep':
        case 'smarttipStep':
        case 'featureStep':
            let ImageEditorToolHeight = mg$('.mgPlayerJSProd_howto-paint-tool-container').innerHeight() || 0;
            containerHeight = panelHeight - headerHeight - headerActionBoxHeight - stepEditFooterHeight - footerHeight - ImageEditorToolHeight;
            mg$('.mgPlayerJSProd_step-edit-footer-wrapper').css('bottom', footerHeight);
            mg$('.mgPlayerJSProd_category-list').innerHeight(containerHeight);
            break;

        case 'addGuide':
        case 'addVideo':
            let headderTitleHeight = mg$('.mgPlayerJSProd_header-title').innerHeight() || 0;
            containerHeight = panelHeight - (panelHeaderHeight + headderTitleHeight + bottomButtonWrapper + footerHeight);
            mg$('.mgPlayerJSProd_tour-btn-wrapper').css('bottom', footerHeight);
            mg$('.mgPlayerJSProd_guide-form-wrapper').innerHeight(containerHeight);
            break;

        case 'textSlide':
            let headderStepWrapperHeight = mg$('.mgPlayerJSProd_header-text-step-wrapper').innerHeight() || 0;
            bottomButtonWrapper = mg$('.mgPlayerJSProd_tour-btn-wrapper').innerHeight() || 0;
            containerHeight = panelHeight - (headerHeight + headderStepWrapperHeight + bottomButtonWrapper + footerHeight);
            mg$('.mgPlayerJSProd_guide-form-wrapper').innerHeight(containerHeight);
            mg$('.tox-tinymce').css('height', containerHeight - 160);
            break;

        case 'tasklist':
            let taskListPageHeaderHeight = mg$('.mgPlayerJSProd_task-list-header').innerHeight()
                ? mg$('.mgPlayerJSProd_task-list-header').innerHeight()
                : 0;

            containerHeight = panelHeight - headerHeight - taskListPageHeaderHeight - footerHeight;
            mg$('.mgPlayerJSProd_assign-container').innerHeight(containerHeight);
            break;

        case 'addAiGuideView':
            containerHeight = panelHeight - panelHeaderHeight - footerHeight -
            mg$('.mgPlayerJSProd_header-title').innerHeight();
            mg$('.mgPlayerJSProd_form-wrapper').innerHeight(containerHeight);
            break;
        }    }, 300);
};

rootScope.updateSlideInShowme = function(PS, firstSlideId, lastSlideId) {
    let modifiedPs = [{
        id: firstSlideId,
        tail: PS[0].id,
    }];

    for (let i = 0; i < PS.length; i++) {
        if (!PS[i].branch && !PS[i].tail) {
            PS[i].tail = lastSlideId;
            modifiedPs.push(PS[i]);
        } else {
            modifiedPs.push(PS[i]);
        }
    }

    modifiedPs.push({
        id: lastSlideId,
        tail: null,
    });

    return modifiedPs;
};

rootScope.switchApp = function(app) {
    rootScope.applicationDropdown = false;
    rootScope.automationDropdown = false;

    rootScope.contextTours = {};

    rootScope.resetCreatorData(true);

    rootScope.initAppDetails(app);

    rootScope.selectedApp = app.title;

    if (GmCXt.FT.isPlayer && rootScope.showGuidesFromDesktopApp) {
        rootScope.initPlayerMode();
    }

    Gm.breadCrumb.set([]);
    rootScope.setCurrentPage("allguides");
    Gm.routeAction.set({
        action: "on_app_select",
        app
    });
};

rootScope.switchAppType = function (isDesktop) {
    rootScope.showGuidesFromDesktopApp = isDesktop;

    GmCXt.saveToStorage('showGuidesFromDesktopApp', rootScope.showGuidesFromDesktopApp);

    if (!rootScope.showGuidesFromDesktopApp) {

        let isDomainRecognizedInApp = autoSwitchApp();

        if (!isDomainRecognizedInApp) {
            rootScope.goToNoApp();
        } else {
            rootScope.initPlayerMode();
        }

        return;
    } else {
        let getDefaultDesktopApp = Object.values(GmCXt.appList).filter((app) => app.type === 'electron')[0];
        rootScope.switchApp(getDefaultDesktopApp);
    }
};

rootScope.showLoading = function() {
    Gm.loading.set(true);
};

rootScope.hideLoading = function() {
    GmCXt.activeApiCallCount = Math.max(0, GmCXt.activeApiCallCount);

    GmCXt.timeout(function() {
        // wait one second if another api is triggred simultaneously(Mostly for signin scenario)
        if (GmCXt.activeApiCallCount <= 0) {
            if (GmCXt.loadingAutoHideTimer) {
                clearTimeout(GmCXt.loadingAutoHideTimer);
            }
            Gm.loading.set(false);
        }
    },1000);
};

rootScope.exportCsv = function(tour, ev) {

    function createCsv(filename, steps) {
        let columns = [];
        let csvContent = "data:text/csv;charset=utf-8,";

        let readColumns = function(allSteps, count, interval) {
            if (count === 0) {

                clearInterval(interval);

                allSteps.forEach(function(s) {
                    let auto = s.step_settings.automation;

                    if (auto && auto.enableDefaultData && auto.csvParam) {
                        columns.push(auto.csvParam);
                    }
                });

                csvContent += columns.join(",");

                rootScope.hideLoading();

                filename = filename + '.csv';

                let link = document.createElement('a');
                link.setAttribute('href', encodeURI(csvContent));
                link.setAttribute('download', filename);
                link.click();
            }
        };

        rootScope.checkLinkedGuideStep(steps, readColumns);

    }

    rootScope.showLoading();

    if (!tour.steps.length) {
        GmCXt.getSteps({
            tour_id: tour.tour_id
        }).then(function(t) {
            createCsv(t.tour_title, t.steps);
        });
    } else {
        createCsv(tour.tour_title, tour.steps);
    }

};

