GmCXt.onDapStoreInstanceReceived = function() {
    readStorage().then(() => {
        activeLanguage = rootScope.language;
        GmCXt.getLanguageLabels(activeLanguage, true).then(function () {
            if (!GmCXt.isPlayer()) {
                GmCXt.getLanguageLabels(activeLanguage, false).then(function () {
                    onSidePanelLoad();
                });
            } else {
                onSidePanelLoad();
            }
        }).catch(error => {
            onSidePanelLoad();
        });
        
    }).catch(error => {
        console.error("Initialization error:", error);
    });
};

